DROP TABLE IF EXISTS  `pre_min_agenda`;
CREATE TABLE `pre_min_agenda` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `date_id` tinyint(2) NOT NULL COMMENT '日程ID',
  `is_hot` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否热门，1为热门',
  `time` varchar(20) NOT NULL COMMENT '会议具体时间',
  `theme` varchar(50) NOT NULL DEFAULT '' COMMENT '会议主题',
  `guest` varchar(32) NOT NULL COMMENT '嘉宾',
  `img` varchar(150) DEFAULT NULL COMMENT '图片',
  `auditoria` varchar(50) DEFAULT NULL COMMENT '会议厅',
  `address` varchar(200) NOT NULL COMMENT '会议地址',
  `exhibition_hotel_id` int(11) NOT NULL COMMENT '展位酒店id',
  `infoimg` varchar(150) NOT NULL COMMENT '详情图片',
  `introduce` text NOT NULL COMMENT '介绍',
  `create_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='日程表';

DROP TABLE IF EXISTS  `pre_min_agenda_v2`;
CREATE TABLE `pre_min_agenda_v2` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
