DROP TABLE IF EXISTS  `pre_min_banner`;
CREATE TABLE `pre_min_banner` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `banner` varchar(150) NOT NULL COMMENT '轮播图片',
  `content` varchar(1200) NOT NULL COMMENT '内容',
  `news_category_id` tinyint(1) NOT NULL COMMENT '资讯分类id',
  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '上传时间',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态',
  `editor` varchar(20) NOT NULL COMMENT '编辑者',
  `time` datetime NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='资讯轮播表';

DROP TABLE IF EXISTS  `pre_min_bgimg`;
CREATE TABLE `pre_min_bgimg` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `type` tinyint(2) NOT NULL COMMENT '类型；1酒店，2展馆',
  `hotel_num` int(10) NOT NULL COMMENT '背景属于的酒店个数',
  `group_name` varchar(50) DEFAULT NULL COMMENT '分组名',
  `back_image` varchar(150) NOT NULL COMMENT '背景图片',
  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `status` tinyint(2) NOT NULL DEFAULT '1' COMMENT '状态',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS  `pre_min_booth`;
CREATE TABLE `pre_min_booth` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `company` varchar(50) NOT NULL COMMENT '企业',
  `exhibition_hotel_id` int(11) NOT NULL COMMENT '场所',
  `floor` tinyint(2) NOT NULL COMMENT '几层楼或者几号馆',
  `exhibition_code` varchar(20) NOT NULL COMMENT '展位号',
  `address` varchar(100) NOT NULL COMMENT '地址',
  `contact` varchar(25) NOT NULL COMMENT '联系人',
  `tel` varchar(25) NOT NULL COMMENT '电话',
  `start_time` varchar(30) NOT NULL COMMENT '开始时间',
  `end_time` varchar(30) NOT NULL COMMENT '结束时间',
  `product` varchar(200) DEFAULT NULL COMMENT '产品',
  `img` varchar(150) NOT NULL COMMENT '图片',
  `primary_label` varchar(50) NOT NULL COMMENT '一级标签',
  `two_label` varchar(100) NOT NULL COMMENT '二级标签',
  `three_label` varchar(80) NOT NULL COMMENT '三级',
  `is_hot` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否热门,1 热门',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态',
  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
